import React from 'react';

// Quando o usuário clicar em um dos botões, faça um fetch do produto clicado utilizando a api abaixo
// https://ranekapi.origamid.dev/json/api/produto/notebook
// https://ranekapi.origamid.dev/json/api/produto/smartphone
// Mostre o nome e preço na tela (separe essa informação em um componente Produto.js)
// Defina o produto clicado como uma preferência do usuário no localStorage
// Quando o usuário entrar no site, se existe um produto no localStorage, faça o fetch do mesmo


const App = () => {

  const [dados, setDados] = React.useState(null);

  React.useEffect(() => {

  }, [])

  const handleClick = ({ target }) => {
    if(target.value === 'notebook'){
      const response = fetch('https://ranekapi.origamid.dev/json/api/produto/notebook')
      const data = response.json();
      setDados(data);
    } else {
      const response = fetch('https://ranekapi.origamid.dev/json/api/produto/smartphone')
      const data = response.json();
      return data;
    }
  }

  return (
    <div>
      <button onClick={ handleClick } value="notebook">Notebook</button>
      <button onClick={ handleClick } value="smartphone">Smartphone</button>

      {dados}
    </div>
  )
};

export default App;
