import React from 'react'

function Radio({pergunta, options, active}) {
/*   if(active === false) return null; */
  return (
    <fieldset style={{
      padding: '2rem',
      marginBottom: '1rem',
      border: '2px solid #eee',
    }}>
      <legend style={{ fontWeight: 'bold' }}>{pergunta}
      {options.map((option, index) => (
        <label key={index}>
          <input type="radio" value={option} />
          {option}
        </label>
      ))}
      </legend>
    </fieldset>
  )
}

export default Radio
