import React from 'react'

function Radio({pergunta, options, active}) {
  if(active === false) return null;
  return (
    <fieldset>
      <legend>{pergunta}</legend>
      {options.map((option) => (
          <input key={option} type="radio" value={option} />
      ))}
    </fieldset>
  )
}

export default Radio
