import React from 'react'

function Radio({pergunta, options, active, onChange, resposta}) {
  if(active === false) return null;
  return (
    <fieldset style={{
      padding: '2rem',
      marginBottom: '1rem',
      border: '2px solid #eee',
    }}>
      <legend style={{ fontWeight: 'bold' }}>{pergunta}</legend>
      {options.map((option, index) => (
        <label key={index}>
          <input type="radio" checked={option === resposta} value={option} />
          {option}
        </label>
      ))}
    </fieldset>
  )
}

export default Radio
